import { AppContext } from 'App'
import React from 'react'
import { useContext, useState } from 'react'

function NewFilm({currentUser}) {
  const [slideInfo, setSlideInfo] = useState({
    city:'',
    country:'',
    date:'',
    note:''
  })
  const {city, country, date, note} = slideInfo

  const [errors, setErrors] = useState([])
  // const [image, setimage] = useContext(AppContext)


  function handleSubmit(e) {
    e.preventDefault()
    const slideshow = {city, country, date, note, user_id: currentUser.id}

    fetch('/slideshows',{
      method: 'POST',
      headers: {"Content-type": "application/json"},
      body: JSON.stringify(slideshow)
    }).then(res => {
      if (res.ok) {
          res.json().then(slide => {
              // updateSlide(slide)
              console.log(slide)
          })
      } else {
          res.json().then(json => setErrors(Object.entries(json.errors)))
      }
    })

    // const data = new FormData()

    // data.append("post[image]", e.target.image.files[49]);
    // submitToAPI(data);
    e.target.reset()
  }

  function submitToAPI(data) {

  }

  function handleChange(e) {
    const { name, value } = e.target
    setSlideInfo({ ...slideInfo, [name]: value })
  }

  return (
    <div>
      <form onSubmit={(e) => handleSubmit(e)}>
        <input 
          type='text'
          name='city'
          placeholder='City name' 
          value={city}
          onChange={handleChange}
        />
        <input 
          type='text'
          name='country'
          placeholder='Country name' 
          value={country}
          onChange={handleChange}
        />
        <input 
          type='text'
          name='date'
          placeholder='year.month.date' 
          value={date}
          onChange={handleChange}
        />
        <input 
          type='text'
          name='note'
          placeholder='Note' 
          value={note}
          onChange={handleChange}
        />
        {/* <input
          type='file'
          name='image'
          id='image'
        /> */}
        <button type='submit'>Create Film</button>
      </form>
      {errors? errors.map(error => <div> {error[0]} {error[1]} </div>) :null}
    </div>
  )
}

export default NewFilm
