// @ts-nocheck
import React, { useRef, useEffect } from 'react'
import mapboxgl from 'mapbox-gl'
import { MapContainerStyle } from 'Styles/Map.style'
import MovieCard from './MovieCard'

mapboxgl.accessToken='pk.eyJ1IjoiY3lvb2ppbjUxMyIsImEiOiJjbDhvd3VtdjgwMXc2M29xbWVxOTNmMXhnIn0.lf4m7kWS3gbVN6BWe9kOBA'

function MapCard({movie}) {

  const mapContainer = useRef()

  useEffect(() => {
    function handleMarkers() {
      let object = []
      for (let i = 0; i < movie.length; i++) {
        const array = {
          'type': 'Feature',
          'properties': {
            'description': `<h2>${movie[i].city}</h2>`
          },
          'geometry': {
            'type': 'Point',
            'coordinates': [movie[i].lon, movie[i].lat]
          }
        }
        object.push(array) 
      }
      return object
    }

    const map = new mapboxgl.Map({
      container: mapContainer.current,
      style: 'mapbox://styles/cyoojin513/cl8q8k0yf006615mv5q0njtfk',
      center: [-98.676392, 39.106667],
      zoom: 2,
      attributionControl: false
    })

    map.on('load', () => {
      map.addSource('mapcard', {
        'type': 'geojson',
        'data': {
          'type': 'FeatureCollection',
          'features':  handleMarkers()
        }
      })

      map.addLayer({
        'id': 'dots',
        'type': 'circle',
        'source': 'mapcard',
        'paint': {
          'circle-color': 'red',
          'circle-radius': 6,
        }
      })

      // map.on('click', 'dots', (e) => {
      //   const coordinates = e.features[0].geometry.coordinates.slice();
      //   const description = e.features[0].properties.description;

      //   new mapboxgl.Popup()
      //   .setLngLat(coordinates)
      //   .setHTML(description)
      //   .addTo(map);
      // })

      map.on('loads', () => {
        const popup = new mapboxgl.Popup()
        .setLngLat([-96, 37.8])
        .setHTML('<h1>Hello World!</h1>')
        .addTo(map);
      })

      map.on('mouseenter', 'dots', () => {
        map.getCanvas().style.cursor = 'pointer'
        })

      map.on('mouseleave', 'dots', () => {
        map.getCanvas().style.cursor = ''
        })
    })

    console.log('Map useEffect')
  }, [])



 return (
  <MapContainerStyle>
    <link href="https://api.mapbox.com/mapbox-gl-js/v2.6.1/mapbox-gl.css" rel="stylesheet"/>
    <div ref={mapContainer} className='map-container' style={{width:'100%', height:'80vh'}}></div>
  </MapContainerStyle>
 )
}

export default MapCard
