import styled from "styled-components"

const FormContainer = styled.div`
  display: grid;
  grid-template-rows: 60px 300px 200px;
  justify-content: center;
  /* align-items: center; */
  margin-top: 50px;

  .first-row {
    display: grid;
    grid-row: 1;
    grid-template-columns: 1fr 1fr;
  }

  input::placeholder {
    color: #BEBEBE;
  }

  input[name=address], input[name=date] {
    background-color: #282828;
    color: #BEBEBE;
    width: 330px;
    height: 20px;
    padding: 12px 20px;
    margin: 8px;
    border: none;
    border-radius: 18px;
  }

  .second-row {
    display: grid;
    grid-row: 2;
  }

  textarea[name=note] {
    background-color: #282828;
    color: #BEBEBE;
    width: 752px;
    height: 200px;
    margin: 8px;
    border: none;
    border-radius: 30px;
    resize: none;
  }
`

export { FormContainer }
