// @ts-nocheck
import React, { useRef, useEffect } from 'react'
import { Route, Switch, useHistory } from "react-router-dom";
import { useState } from 'react'
import Signup from "./Components/Signup";
import Login from "./Components/Login";
import UserPage from "./Components/UserPage";
import NewFilm from "./Components/NewFilm";
import NavBar from "./Components/NavBar";
import PostPhotos from './Components/PostPhotos';
import Movie from 'Components/Movie';
import EditFilm from './Components/EditFilm';
import LoginError from './Components/LoginError';
// Styling
import { AppContainer, SecondRow } from './Styles/App.style'


function App() {

  const [ currentUser, setCurrentUser ] = useState("")
  const [ slideshows, setSlideshows ] = useState([])
  const [ currentSlideId, setCurrentSlideId ] = useState(null)

  const history = useHistory()

  useEffect(() => {
    if (!currentUser) {
      fetch('/me').then((res) => {
        if (res.ok) {
          res.json().then((data) => setCurrentUser(data))
        }
      })
    }
  }, [])


  useEffect(() => {
    fetch('/slideshows')
    .then((res) => {
      if (res.ok) {
        res.json().then((data) => setSlideshows(data))
      }
    })
  }, [ currentUser ])

  const mapContainer = useRef()

  useEffect(() => {
    function handleMarkers() {
      let object = []
      for (let i = 0; i < movie.length; i++) {
        const array = {
          'type': 'Feature',
          'geometry': {
            'type': 'Point',
            'coordinates': [movie[i].lon, movie[i].lat]
          }
        }
        object.push(array) 
      }
      console.log(object)
      return object
    }


    const map = new mapboxgl.Map({
      container: mapContainer.current,
      style: 'mapbox://styles/cyoojin513/cl8q8k0yf006615mv5q0njtfk',
      center: [-98.676392, 39.106667],
      zoom: 2,
      attributionControl: false
    })

    map.on('load', () => {
      map.addSource('mapcard', {
        'type': 'geojson',
        'data': {
          'type': 'FeatureCollection',
          'features':  handleMarkers()
        }
      })

      map.addLayer({
        'id': 'dots',
        'type': 'circle',
        'source': 'mapcard',
        'paint': {
          'circle-color': 'red',
          'circle-radius': 6,
        }
      })
    })
  }, [])

  function updateUser(user) {
    setCurrentUser(user)
  }

  function deleteRendered(id) {
    setSlideshows(slideshows?.filter((item) => item.id != id))
  }

  function updateSlideshows(newMovie) {
    setSlideshows([...slideshows, newMovie])
  }

  function updatingIsReleased(updatedSlide) {
    const updated = slideshows.map((item) =>{
      if (item.id === updatedSlide.id) {
        return updatedSlide
      } else {return item} 
    })
    setSlideshows(updated)
  }

  function getSlideId(id) {
    setCurrentSlideId(id)
  }

  return (
    <div>
        <AppContainer>
          <NavBar currentUser={currentUser} updateUser={updateUser}/>
          <SecondRow>
            <Switch>
              <Route path='/user/:id'>
                <UserPage currentUser={currentUser} slideshows={slideshows}/>
              </Route>
              <Route path='/movie/:id'>
                <Movie currentUser={currentUser} deleteRendered={deleteRendered}/>
              </Route>
              <Route path='/newfilm'>
                <NewFilm
                  currentUser={currentUser}  
                  updateSlideshows={updateSlideshows}
                  getSlideId={getSlideId}  
                />
              </Route>
              <Route path='/editfilm/:id'>
                <EditFilm 
                  currentUser={currentUser}
                  updatingIsReleased={updatingIsReleased}
                  getSlideId={getSlideId}  
                />
              </Route>
              <Route path='/postphotos'>
                <PostPhotos
                  currentSlideId={currentSlideId}
                  currentUser={currentUser}
                  updatingIsReleased={updatingIsReleased}
                />
              </Route>
              <Route path='/loginerror'>
                <LoginError />
              </Route>
              <Route path="/signup">
                <Signup updateUser={updateUser} />
              </Route>
              <Route exact path="/">
                <Login updateUser={updateUser} />
              </Route>
            </Switch>
          </SecondRow>
        </AppContainer>
    </div>
  );
}


export default App;
