import React from 'react'
import { useState } from 'react'
import { useHistory } from 'react-router-dom'

function NewFilm({currentUser, updateSlideshows, getSlideId}) {
  const [ address, setAddress ] = useState("")
  const [ date, setDate ] = useState("")
  const [ note, setNote ] = useState("")

  const [ errors, setErrors ] = useState([])
  const history = useHistory()

  function getGeocode() {
    const url = 'https://geocoder.ls.hereapi.com/6.2/geocode.json?searchtext=' + address + '&gen=9apiKey=_UflM7c4oF5vFzs30fh5ZNLsEmovcCI6tSagg2YLaSM'
    const xmlHttp = new XMLHttpRequest()
    xmlHttp.open("GET", url, false)
    xmlHttp.send(null)
    const json = JSON.parse(xmlHttp.responseText)
    console.log(json)
  }


  function handleSubmit(e) {
    e.preventDefault()
    getGeocode()
    const slideshow = {
      address: address,
      date: date, 
      note: note, 
      user_id: currentUser.id,
      isReleased: false
    }
    // handlePost(slideshow, `/postphotos`)
  }

  function handlePost(item, urlAddress) {
    fetch('/slideshows',{
      method: 'POST',
      headers: {"Content-type": "application/json"},
      body: JSON.stringify(item)
    }).then(res => {
      if (res.ok) {
          res.json().then(slide => {
            updateSlideshows(slide)
            getSlideId(slide.id)
            history.push(urlAddress)
          })
      } else {
          res.json().then(json => setErrors(Object.entries(json.errors)))
      }
    })
  }

  return (
    <div>
      <form onSubmit={(e) => handleSubmit(e)}>
        <input 
          type='text'
          name='address'
          placeholder='Address' 
          value={address}
          onChange={(e) => setAddress(e.target.value)}
        />
        <input 
          type='text'
          name='date'
          placeholder='year.month.date' 
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />
        <input 
          type='text'
          name='note'
          placeholder='Note' 
          value={note}
          onChange={(e) => setNote(e.target.value)}
        />
        <button type='submit'>Next</button>
      </form>
      <button onClick={getGeocode}>geocode</button>
      {errors? errors.map(error => <div> {error[0]} {error[1]} </div>) :null}
    </div>
  )
}

export default NewFilm
