import styled from "styled-components"

const PostPhotoContainer = styled.div`
  display: grid;
  grid-template-rows: 60px 230px 200px;
  justify-content: center;
  margin-top: 35px;

  .first-row {
    grid-row: 1;

    input[name=image] {
      background-color: #282828;
    color: #BEBEBE;
    font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
    width: 330px;
    height: 25px;
    padding: 12px 20px;
    margin: 8px;
    border: none;
    border-radius: 25px;
    }
  }

  .renderedPhotos {
    border-radius: 15px;
    width: 100px;
    height: 100px;
    object-fit: cover;

  }
`

export { PostPhotoContainer }
