import styled from "styled-components"

const PostPhotoContainer = styled.div`
  display: grid;
  grid-template-rows: 60px 230px 200px;
  grid-template-columns: 30px 1fr 30px;
  justify-content: center;
  margin-top: 35px;

  form {
    display: grid;
    grid-column: 2;
  }

  .first-row {
    display: grid;
    grid-row: 1;

    h2 {
      font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
      font-weight: 100;
      color: #838383;
      margin: 0px
    }

    input::file-selector-button {
      background-color: #282828;
      color: #BEBEBE;
      font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
      width: 350px;
      height: 45px;
      border: none;
      border-radius: 25px;
    }
  }

  .renderedPhotos {
    border-radius: 15px;
    width: 100px;
    height: 100px;
    object-fit: cover;

  }
`

export { PostPhotoContainer }
