import styled from "styled-components"

const MovieContainer = styled.div`
  display: grid;
  grid-template-columns: auto 800px auto;
  color: #8A8A8A;

  .center-grid {
    display: grid;
    grid-column: 2;
  }

  .slideshow {
    width: 350px;
    height: 500px;
    object-fit: cover;
  }
`

export { MovieContainer }
