import styled from "styled-components"

const MovieContainer = styled.div`
  display: grid;
  grid-template-columns: auto 750px auto;
  margin: 80px 0px;
  color: #8A8A8A;

  .center-grid {
    display: grid;
    grid-column: 2;
    grid-template-columns: 370px 30px 360px 30px;

    .first-column {
      display: grid;
      grid-template-rows: 300px 1fr;

      .city-country-container {
        display: grid;
        grid-row: 1;
      }

      h1 {
        font-family: "Gill Sans", sans-serif;
        font-size: 60px;
        font-weight: 100;
        color: #F1F1F1;
        padding-bottom: -70%;
        margin-top: 0px;
      }
    }

    .second-column {
      display: grid;
      grid-column: 2;

      button {
        width: 18px;
        height: 18px;
      }
    }


  }
  .photo {
    width: 360px;
    height: 500px;
    object-fit: cover;
  }
`

export { MovieContainer }
