// @ts-nocheck
import React, { useRef, useEffect } from 'react'
import { useHistory } from 'react-router-dom'
import PreLeaseCard from './PreLeaseCard'
import mapboxgl from 'mapbox-gl'
import MapCard from './MapCard'
// Styling
import { MovieContainer, PreReleaseContainer , CardContainer } from 'Styles/UserPage.style'
import { MapContainerStyle } from 'Styles/Map.style'

mapboxgl.accessToken='pk.eyJ1IjoiY3lvb2ppbjUxMyIsImEiOiJjbDhvd3VtdjgwMXc2M29xbWVxOTNmMXhnIn0.lf4m7kWS3gbVN6BWe9kOBA'

function UserPage({currentUser, slideshows}) {

  const history = useHistory()

  useEffect(() => {
    if (!currentUser) {
      fetch('/me').then((res) => {
        if (!res.ok) {
            history.push('/loginerror')
        }
      })
    }
  }, [])

  const releasedMovie = slideshows.filter((item) => item.isReleased)

  const mapContainer = useRef()

  useEffect(() => {
    function handleMarkers() {
      let object = []
      for (let i = 0; i < releasedMovie.length; i++) {
        const array = {
          'type': 'Feature',
          'geometry': {
            'type': 'Point',
            'coordinates': [releasedMovie[i].lon, releasedMovie[i].lat]
          }
        }
        object.push(array) 
      }
      return object
    }

    const map = new mapboxgl.Map({
      container: mapContainer.current,
      style: 'mapbox://styles/cyoojin513/cl8q8k0yf006615mv5q0njtfk',
      center: [-98.676392, 39.106667],
      zoom: 2,
      attributionControl: false
    })

    map.on('load', () => {
      map.addSource('mapcard', {
        'type': 'geojson',
        'data': {
          'type': 'FeatureCollection',
          'features':  handleMarkers()
        }
      })

      map.addLayer({
        'id': 'dots',
        'type': 'circle',
        'source': 'mapcard',
        'paint': {
          'circle-color': 'red',
          'circle-radius': 6,
        }
      })
    })
    console.log('Map useeffect')
  }, [])

  return (
    <div>
      <MovieContainer>
        <h1 className='my-movie-title'>My Movies</h1>
        {/* <MapCard movie={releasedMovie} /> */}
        <MapContainerStyle>
    <div ref={mapContainer} className='map-container' style={{width:'100%', height:'80vh'}}></div>
  </MapContainerStyle>
      </MovieContainer>
      <PreReleaseContainer>
          <h1 className='pre-release-title'>Pre-release Films</h1>
          <CardContainer>
            {slideshows.map((slide) => slide.isReleased
              ? null
              : <PreLeaseCard
                  key = {slide.id}
                  id = {slide.id}
                  city = {slide.city}
                  country = {slide.country}
                  date = {slide.date}
                />
            )}
        </CardContainer>
      </PreReleaseContainer>
    </div>
  )
}

export default UserPage
