import styled from "styled-components"

const MainContainer = styled.div`
  display: grid;
  grid-template-columns: auto 280px auto;

  /* .cardGrid {
    grid-column: 2;
    display: grid;
    grid-template-rows: 860px 300px;
  }   */

  .thecard {
    grid-column: 2;
    display: grid;
    grid-template-rows: 860px 300px;
    /* position: absolute;
    width: 100%;
    height: 100%; */
  }

  .login-form {
    width: 280px;
    height: 300px;
    background: #ffc728;
  }

  .signup-form {
    position: absolute;
    width:100%;
    height: 100%;
    backface-visibility: hidden;
    background: #ffc728;
    color: #333;
  }
`

export { MainContainer }
