import React, { useState } from 'react'
import { Route, Switch } from "react-router-dom";
import MovieCard from './MovieCard'
import Movie from './Movie'
import PreLeaseCard from './PreLeaseCard'

function UserPage({currentUser, slideshows}) {

  const [renderslide, setRenderSlide] = useState(slideshows)

  function deleteRendered(id) {
    const updatedList = slideshows.filter((item) =>
      item.id !== id
    )
    setRenderSlide(updatedList)
  }

  return (
    <div>
      <h1>My Movies</h1>
      <div>
        <Switch>
          <Route path='/movie/:id'>
            <Movie currentUser={currentUser} deleteRendered={handleDeletedId}/>
          </Route>
        </Switch>
        {renderslide.map((slide) => slide.isReleased
          ? <MovieCard
              key = {slide.id}
              id = {slide.id}
              city = {slide.city}
              country = {slide.country}
              date = {slide.date}
            />
          : null
        )}
      </div>
      <h1>Pre-release Films</h1>
      <div>
        {renderslide.map((slide) => slide.isReleased
          ? null
          : <PreLeaseCard
              key = {slide.id}
              id = {slide.id}
              city = {slide.city}
              country = {slide.country}
              date = {slide.date}
            />
        )}
      </div>
    </div>
  )
}

export default UserPage
