import styled from "styled-components"

const PostPhotoContainer = styled.div`
  display: grid;
  grid-template-columns: auto 750px auto;
  margin-top: 35px;

  .center-grid {
    display: grid;
    grid-column: 2;
    grid-template-columns: 400px 350px;

    .first-column {
