import styled from "styled-components"

const MainContainer = styled.div`
  display: grid;
  grid-template-columns: auto 300px auto;

  .card-grid {
    grid-column: 2;
    display: grid;
    /* position: absolute;
    width: 100%;
    height: 100%; */
  }

  .login-form {
    margin-top: 150px;
    width: 300px;
    height: 360px;
    background: #0F0F0F;
    border-radius: 40px;

  }



  .signup-form {
    position: absolute;
    width:100%;
    height: 100%;
    backface-visibility: hidden;
    background: #ffc728;
    color: #333;
  }
`

export { MainContainer }
