import React, { useState } from 'react'
import MovieCard from './MovieCard'
import PreLeaseCard from './PreLeaseCard'

function UserPage({slideshows, deleteRendered}) {

  const [renderslide, setRenderSlide] = useState(slideshows)

  function deleteRendered(id) {
    const updatedList = slideshows.filter((item) =>
      item.id !== id
    )
    setRenderSlide(updatedList)
  }

  return (
    <div>
      <h1>My Movies</h1>
      <div>
        {renderslide.map((slide) => slide.isReleased
          ? <MovieCard
              key = {slide.id}
              id = {slide.id}
              city = {slide.city}
              country = {slide.country}
              date = {slide.date}
            />
          : null
        )}
      </div>
      <h1>Pre-release Films</h1>
      <div>
        {renderslide.map((slide) => slide.isReleased
          ? null
          : <PreLeaseCard
              key = {slide.id}
              id = {slide.id}
              city = {slide.city}
              country = {slide.country}
              date = {slide.date}
            />
        )}
      </div>
    </div>
  )
}

export default UserPage
