import styled from "styled-components"

const FormContainer = styled.div`
  display: grid;
  grid-template-rows: 150px 300px 200px;

  .first-row {
    display: grid;
    grid-row: 1;
    grid-template-columns: 1fr 1fr;
  }

  input[name=address] {
    background-color: #282828;
    width: 150px;
  }

  input[name=note] {
    display: grid;
    grid-row: 2;
  }
`

export { FormContainer }
